mapper.select:
SELECT * FROM iam_dashboard WHERE 1=1
AND name LIKE concat(concat('%',#{dashboard.name}),'%')
AND code LIKE concat(concat('%',#{dashboard.code}),'%')
AND fd_level = #{dashboard.level}
AND name LIKE concat(concat('%',#{param}),'%') OR code LIKE concat(concat('%',#{param}),'%') OR fd_level = #{param}
