mapper.select:
SELECT fo.id, fo.name, fo.code, fo.is_enabled, ir.id AS role_id, ir.name AS role_name, ir.code AS role_code, ir.is_enabled AS role_is_enabled FROM ( SELECT fd_organization.* FROM fd_organization INNER JOIN iam_member_role imr ON imr.source_id = fd_organization.id WHERE imr.source_type = "organization" AND imr.member_id = #{id}
AND ( fd_organization.name LIKE concat('%', #{params}, '%') OR fd_organization.code LIKE concat('%', #{params}, '%') )
GROUP BY fd_organization.id ORDER BY max(imr.member_id) ASC LIMIT #{start}, #{size} ) fo INNER JOIN iam_member_role imr ON imr.source_id = fo.id INNER JOIN iam_role ir ON imr.role_id = ir.id WHERE imr.source_type = "organization" AND imr.member_id = #{id}
